import { set, ref } from "firebase/database";
import React from "react";
import { useState } from "react";
import { uid } from "uid";
import { db, auth } from "../firebase-config";
import "../styles/peamineLeht.css";
import { Form } from "react-bootstrap";

const Reminder = () => {
  const [reminder, setReminder] = useState("");
  const [title, setTitle] = useState("");
  const [kp, setKp] = useState("");
  const [alam, setAlam] = useState(4);

  const lisaMeeldetuletus = () => {
    const uidd = uid();
    console.log(kp);
    set(ref(db, `/${auth.currentUser.uid}/${uidd}`), {
      title: title,
      reminder: reminder,
      uidd: uidd,
      kp: kp,
    });
    setReminder("");
  };
  return (
    <div className="lisaMeeldetuletus">
      <input
        placeholder="meeldetuletuse pealkiri"
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <input
        placeholder="meeldetuletus"
        type="text"
        value={reminder}
        onChange={(e) => setReminder(e.target.value)}
      />

      <div className="row">
        <div className="col-md-4">
          <Form.Group controlId="dob">
            <Form.Label>Kuup2ev</Form.Label>
            <Form.Control
              type="date"
              name="dob"
              placeholder="Date of Birth"
              value={kp}
              onChange={(e) => setKp(e.target.value)}
            />
          </Form.Group>
        </div>
      </div>
      <div className="alamEesmargid">
        {[...Array(alam)].map((index) => (
          <p id={index + 1}>tere {index}</p>
        ))}
      </div>
      <button onClick={lisaMeeldetuletus}>lisa</button>
    </div>
  );
};
export { Reminder };
