import React, { useEffect, useState } from "react";
import { signInWithEmailAndPassword, onAuthStateChanged } from "firebase/auth";
import { auth } from "../firebase-config.js";
import { useNavigate } from "react-router-dom";

export default function TereTulemast() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [registreerib, setRegistreerib] = useState(false);
  const navigate = useNavigate();

  const registreeribKasutajat = () => {
    setRegistreerib(true);
  };

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        navigate("/peamineLeht");
      }
    });
  });

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSignIn = () => {
    console.log(auth);
    signInWithEmailAndPassword(auth, email, password)
      .then(() => {
        navigate("/peamineLeht");
      })
      .catch((err) => alert(err.message));
  };
  return (
    <div className="App">
      <h1>Rakendus</h1>
      <div className="login-container">
        {registreerib ? (
          <>
            <input className="emailReg" />
            <input
              className="salas6naReg"
              placeholder="salas6na"
              onChange={handlePasswordChange}
              value={password}
            />
            <button className="sisselogimine" onClick={handleSignIn}>
              {" "}
              Logi sisse{" "}
            </button>
            <button onClick={registreeribKasutajat()}>Tagasi </button>
          </>
        ) : (
          <>
            <input
              className="email"
              placeholder="E-mail"
              onChange={handleEmailChange}
              value={email}
            />
            <input
              className="salas6na"
              placeholder="salas6na"
              onChange={handlePasswordChange}
              value={password}
            />
            <button className="sisselogimine" onClick={handleSignIn}>
              {" "}
              Logi sisse{" "}
            </button>
            <button onClick={registreeribKasutajat()}>
              Registreeri kasutaja
            </button>
          </>
        )}
      </div>
      <button onClick={console.log(registreerib)}>asd</button>
    </div>
  );
}
