import { render } from "@testing-library/react";
import { set, ref } from "firebase/database";
import React from "react";
import { useState } from "react";
import { uid } from "uid";
import { db, auth } from "../firebase-config";
import "../styles/peamineLeht.css";
import { Form } from "react-bootstrap";

const Reminder = () => {
  const [reminder, setReminder] = useState("");
  const [title, setTitle] = useState("");

  const lisaMeeldetuletus = () => {
    const uidd = uid();
    console.log(auth);
    set(ref(db, `/${auth.currentUser.uid}/${uidd}`), {
      title: title,
      reminder: reminder,
      uidd: uidd,
    });
    setReminder("");
  };
  return (
    <div className="lisaMeeldetuletus">
      <input
        placeholder="meeldetuletuse pealkiri"
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <input
        placeholder="meeldetuletus"
        type="text"
        value={reminder}
        onChange={(e) => setReminder(e.target.value)}
      />

      <div className="row">
        <div className="col-md-4">
          <Form.Group controlId="dob">
            <Form.Label>Kuup2ev</Form.Label>
            <Form.Control type="date" name="dob" placeholder="Date of Birth" />
          </Form.Group>
        </div>
      </div>

      <button onClick={lisaMeeldetuletus}>lisa</button>
    </div>
  );
};
export { Reminder };
