import React, { useEffect, useState } from "react";
import { signOut } from "firebase/auth";
import { auth, db } from "../firebase-config";
import { useNavigate } from "react-router-dom";
import { onValue, ref, remove } from "firebase/database";
import "../styles/peamineLeht.css";
import { Form, Card, Container, Col } from "react-bootstrap";
import { Popup } from "../elements/popup";
import { Trash3Fill } from "react-bootstrap-icons";

export default function PeamineLeht() {
  const navigate = useNavigate();
  const [meeldetuletused, setMeeldetuletused] = useState([]);
  const [kasutaja, setKasutaja] = useState("");

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      setKasutaja(user.email);
      if (user) {
        onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
          const data = snapshot.val();
          setMeeldetuletused([]);
          if (data != null) {
            Object.values(data).map((meeldetuletus) => {
              setMeeldetuletused((oldArray) => [...oldArray, meeldetuletus]);
            });
          }
        });
      }

      if (!user) {
        navigate("/");
      }
    });
  }, []);

  const logiValja = () => {
    signOut(auth)
      .then(navigate("/"))
      .catch((err) => {
        alert(err.message);
      });
  };

  const kustuta = (todo) => {
    remove(ref(db, `/${auth.currentUser.uid}/${todo.uidd}`));
  };
  return (
    <div>
      <Container className="bg-secondary konteiner">
        <Col>
          <Popup className="align-center" />
        </Col>
        <Col>
          <div className="meeldetuletusteKonteiner">
            {meeldetuletused.map((yks) => (
              <Card className="  m-1">
                <Card.Header as="h3" className="d-flex justify-content-between">
                  {yks.title}
                  <Trash3Fill className="" onClick={() => kustuta(yks)} />
                </Card.Header>
                <div className="display-flex border">
                  <Container>
                    <p>{yks.reminder}</p>
                  </Container>
                  <p>{yks.kp}</p>
                  <Form>
                    {yks.alam.map((alam, i) => (
                      <div className=" ">
                        <label>
                          <input type="checkbox" id={i} value={i} />
                          {alam}
                          <br />
                        </label>
                      </div>
                    ))}
                  </Form>
                </div>
              </Card>
            ))}
          </div>
        </Col>
      </Container>
    </div>
  );
}
