import React, { useEffect, useState } from "react";
import { signOut } from "firebase/auth";
import { auth, db } from "../firebase-config";
import { useNavigate } from "react-router-dom";
import { Reminder } from "../elements/remind";
import { onValue, ref, remove } from "firebase/database";
import "../styles/peamineLeht.css";
import {
  Form,
  Card,
  Navbar,
  Offcanvas,
  NavDropdown,
  Container,
  Button,
} from "react-bootstrap";
import { Popup } from "../elements/popup";
import { Trash3Fill } from "react-bootstrap-icons";

export default function PeamineLeht() {
  const navigate = useNavigate();
  const [meeldetuletused, setMeeldetuletused] = useState([]);
  const [kasutaja, setKasutaja] = useState("");

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      setKasutaja(user.email);
      if (user) {
        onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
          const data = snapshot.val();
          setMeeldetuletused([]);
          if (data != null) {
            Object.values(data).map((meeldetuletus) => {
              setMeeldetuletused((oldArray) => [...oldArray, meeldetuletus]);
            });
          }
        });
      }

      if (!user) {
        navigate("/");
      }
    });
  }, []);

  const logiValja = () => {
    signOut(auth)
      .then(navigate("/"))
      .catch((err) => {
        alert(err.message);
      });
  };

  const kustuta = (todo) => {
    remove(ref(db, `/${auth.currentUser.uid}/${todo.uidd}`));
  };
  return (
    <div>
      <Navbar bg="white" sticky="top">
        <Container>
          <Navbar.Brand href="#">Eesmärk</Navbar.Brand>
          <Navbar.Collapse className="justify-content-end">
            <NavDropdown title={kasutaja}>
              <NavDropdown.Item href="#action3">Muuda andmeid</NavDropdown.Item>
              <NavDropdown.Item href="#action4">Statistika</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item onClick={logiValja}>
                Logi välja{" "}
              </NavDropdown.Item>
            </NavDropdown>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Container className="bg-secondary">
        <div className="d-flex">
          <Popup className="align-center" />
          {meeldetuletused.map((yks) => (
            <Card className=" w-50 m-1">
              <Card.Header as="h3" className="d-flex justify-content-between">
                {yks.title}
                <Trash3Fill className="" onClick={() => kustuta(yks)} />
              </Card.Header>
              <div className="display-flex border">
                <p>{yks.reminder}</p>
                <p>{yks.kp}</p>
                <Form>
                  {yks.alam.map((alam, i) => (
                    <div className=" ">
                      <label>
                        <input type="checkbox" id={i} value={i} />
                        {alam}
                        <br />
                      </label>
                    </div>
                  ))}
                </Form>
              </div>
            </Card>
          ))}
        </div>
      </Container>
    </div>
  );
}
