import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/tereTulemast.css";
import { signOut } from "firebase/auth";
import { auth, db } from "../firebase-config";
import { useState } from "react";
import { Navbar, Container, NavDropdown } from "react-bootstrap";

const MyNavbar = (props) => {
  const [kasutaja, setKasutaja] = useState("");

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      setKasutaja(user.email);
      if (user) {
        onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
          const data = snapshot.val();
          setMeeldetuletused([]);
          if (data != null) {
            Object.values(data).map((meeldetuletus) => {
              setMeeldetuletused((oldArray) => [...oldArray, meeldetuletus]);
            });
          }
        });
      }

      if (!user) {
        navigate("/");
      }
    });
  }, []);

  return (
    <div>
      <Navbar className="navi" sticky="top">
        <Container>
          <Navbar.Brand href="#">Eesmärk</Navbar.Brand>
          <Navbar.Collapse className="justify-content-end">
            <NavDropdown title={props.kasutaja}>
              <NavDropdown.Item href="#action3">Muuda andmeid</NavDropdown.Item>
              <NavDropdown.Item href="#action4">Statistika</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item onClick={props.logiValja}>
                Logi välja{" "}
              </NavDropdown.Item>
            </NavDropdown>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </div>
  );
};
export { MyNavbar };
