import React, { useEffect, useState } from "react";
import {
  signInWithEmailAndPassword,
  onAuthStateChanged,
  createUserWithEmailAndPassword,
} from "firebase/auth";
import { auth } from "../firebase-config.js";
import { useNavigate } from "react-router-dom";
import "../styles/tereTulemast.css";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";

export default function TereTulemast() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [registreerib, setRegistreerib] = useState(false);
  const navigate = useNavigate(false);
  const [regInfo, setRegInfo] = useState({
    remail: "",
    confirmRemail: "",
    rpassword: "",
    confirmRpassword: "",
  });

  useEffect(() => {
    auth.onAuthStateChanged((user) => {
      if (user) {
        navigate("/peamineLeht");
      }
    });
  });

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };
  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSignIn = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then(() => {
        navigate("/peamineLeht");
      })
      .catch((err) => alert(err.message));
  };

  const looKasutaja = () => {
    if (
      regInfo.remail !== regInfo.confirmRemail ||
      regInfo.rpassword !== regInfo.confirmRpassword
    ) {
      alert("Viga e-maili v6i salas6na sisestamisel");
      return;
    }
    createUserWithEmailAndPassword(auth, regInfo.remail, regInfo.rpassword)
      .then(() => {
        navigate("/peamineLeht");
      })
      .catch((err) => alert(err.message));
  };
  return (
    <div className="App loginkonteiner">
      <div className="">
        {registreerib ? (
          <>
            <Container className="p-3 d-flex  justify-content-center w-50">
              <div className="text-center">
                <h1 className="w-100 ">Uue kasutaja registreerimine</h1>

                <input
                  className="emailReg w-50 m-1"
                  placeholder="E-mail"
                  value={regInfo.remail}
                  onChange={(e) =>
                    setRegInfo({ ...regInfo, remail: e.target.value })
                  }
                />
                <input
                  className="emailReg w-50 m-1"
                  placeholder="Korda E-maili"
                  value={regInfo.confirmRemail}
                  onChange={(e) =>
                    setRegInfo({ ...regInfo, confirmRemail: e.target.value })
                  }
                />
                <input
                  className="salas6naReg w-50 m-1"
                  placeholder="Vali salasõna"
                  type="password"
                  value={regInfo.rpassword}
                  onChange={(e) =>
                    setRegInfo({ ...regInfo, rpassword: e.target.value })
                  }
                />
                <input
                  className="salas6naReg w-50 m-1"
                  type="password"
                  placeholder="Korda valitud salasõna"
                  value={regInfo.confirmRpassword}
                  onChange={(e) =>
                    setRegInfo({ ...regInfo, confirmRpassword: e.target.value })
                  }
                />
                <div className="w-100">
                  <Button className=" w-50 my-2" onClick={looKasutaja}>
                    {" "}
                    Registreeri{" "}
                  </Button>
                </div>
                <a
                  className=" my-1"
                  href="#"
                  onClick={() => setRegistreerib(false)}
                >
                  Logi hoopis sisse{" "}
                </a>
              </div>
            </Container>
          </>
        ) : (
          <>
            <Container className="p-3 my-5 d-flex  justify-content-center w-50">
              <div className="text-center">
                <h1 className="w-75 my-5">Tere tulemast!</h1>

                <input
                  className="email w-75 my-1"
                  placeholder="E-mail"
                  onChange={handleEmailChange}
                  value={email}
                />
                <input
                  className="salas6na w-75 my-1"
                  placeholder="Salasõna"
                  type="password"
                  onChange={handlePasswordChange}
                  value={password}
                />
                <Button
                  className="sisselogimine w-75 my-3"
                  onClick={handleSignIn}
                >
                  Logi sisse
                </Button>
                <div>
                  <a href="#" onClick={() => setRegistreerib(true)}>
                    Registreeri selle asemel kasutaja
                  </a>
                </div>
              </div>
            </Container>
          </>
        )}
      </div>
    </div>
  );
}
